// Default imports
import React, {Component} from 'react';

// Custom imports
import axios from 'axios';
import './portfolio.css';
import Nav from '../navbar/navbar';
import {GoGitBranch} from "react-icons/go";
import {connect} from 'react-redux';
import * as personalInfo from '../../actions/action';

class Portfolio extends Component {
    // default constructor
    constructor() {
        super();
        this.state = {
            gitData: [],
            loader: true
        }
    };

    // Form load
    componentDidMount() {
        // Get data from github api here
        axios
            .get('https://api.github.com/users/neeravpanchal26/repos')
            .then(data => this.setState({gitData: data.data, loader: false}))
            .catch(error => console.log(error));
    };

    renderLoader = () => {
        if (this.state.loader) {
            return (
                <div className="progress">
                    <div className="indeterminate"></div>
                </div>
            );
        } else {
            return null;
        }
    };

    renderRepos = () => {
        return this.state.gitData
            .reverse()
            .map(repo => (
                // Html build here
                <div className='card hoverable'>
                    <div className='card-content'>
                        <a href={repo.html_url} className="card-title">{repo.name}</a>
                        <p>{repo.description}</p>
                        <div className="card-action">
                            <span className='blue-text'>{repo.language}</span>
                            <span> â€¢ Latest commit at: {new Date(repo.pushed_at).toUTCString().slice(0, 25)}</span>
                            <span className='right'>
                                {repo.default_branch}
                                <GoGitBranch size={14}/>
                        </span>
                        </div>
                    </div>
                </div>
            ));
    };

    render() {
        return (
            // HTML here
            <div>
                <Nav/>
                <div className='container'>
                    <div className="section">
                        <div className='row'>
                            <h4>My Projects</h4>
                        </div>
                    </div>
                    <div className="divider"></div>
                    {this.renderLoader()}
                    {this.renderRepos()}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return state
};

// Default export
export default connect(mapStateToProps, personalInfo)(Portfolio);
