// Default imports
import React, {Component} from 'react';
import Nav from "../navbar/navbar";
import Footer from "../../components/footer/footer";

// Custom imports
import './timeline.css';
import Loader from "../../components/loader/loader";
import {connect} from "react-redux";
import * as Action from "../../actions/action";

class Timeline extends Component {
    // Component load
    componentDidMount() {
        this.props.GetEvents();
    }

    renderLoader = () => {
        const {pending} = this.props;
        if (pending) {
            return <Loader/>
        } else {
            return null;
        }
    }

    renderEvents = () => {
        const {events} = this.props;
        return events
            .reverse()
            .map(({id,title, eventNameTwo, eventFrom, eventTo, eventType, eventDescription, eventIcon}) => (
                <div className="timeline-event" id={id}>
                    <div className="card timeline-content">
                        <div className="card-content">
                            <span className="card-title">{title}
                                <i className="material-icons activator right">more_horiz</i>
                            </span>
                            <p>{eventNameTwo}</p>
                            <div className='card-action'>
                                <span>{eventFrom} - {eventTo} • {eventType}</span>
                            </div>
                        </div>
                        <div className='card-reveal'>
                            <span className="card-title">{title}<i className="material-icons right">close</i></span>
                            <span dangerouslySetInnerHTML={{__html: eventDescription}}>
                        </span>
                        </div>
                    </div>
                    <div className="timeline-badge blue white-text"><i className="material-icons">{eventIcon}</i>
                    </div>
                </div>
            ));
    };

    render() {
        return (
            // HTML here
            <div>
                <Nav/>
                {this.renderLoader()}
                <div className='container'>
                    <div className="section">
                        <div className='row'>
                            <h4>Progress...</h4>
                        </div>
                    </div>
                    <div className="divider">
                    </div>
                    <div className='timeline'> {this.renderEvents()}</div>
                </div>
                <Footer/>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return state;
};
// Default export
export default connect(mapStateToProps, Action)(Timeline);
