// Default imports
import React, {Component} from 'react';
import './App.css';

// Module imports
import 'materialize-css/dist/css/materialize.css';
import 'materialize-css/dist/js/materialize'
import {IconContext} from "react-icons";

// Component imports
import Routes from "./routes/routes";
import {connect} from "react-redux";
import * as personalInfo from './actions/action';
import Loader from "./components/loader/loader";
import M from 'materialize-css/dist/js/materialize.min.js';

class App extends Component {
    // Component load
    componentDidMount() {
        this.props.GetPersonalInfo();
        const options = {
            onOpenStart: () => {
                console.log("Open Start");
            },
            onOpenEnd: () => {
                console.log("Open End");
            },
            onCloseStart: () => {
                console.log("Close Start");
            },
            onCloseEnd: () => {
                console.log("Close End");
            },
            inDuration: 250,
            outDuration: 250,
            opacity: 0.5,
            dismissible: false,
            startingTop: "4%",
            endingTop: "10%"
        };
        M.Modal.init(this.App, options);
    }

    renderLoader() {
        const {pending} = this.props;
        if (pending) {
            return (
                <div ref={Modal => {
                    this.App = Modal;
                }} className='modal'>
                    <div className='modal-content'>
                        <Loader/>
                    </div>
                </div>
            );
        } else {
            return null;
        }
    }

    render() {
        return (
            // HTML here
            <IconContext.Provider value={{style: {verticalAlign: 'middle'}, size: '24px'}}>
                <div className="App">
                    {this.renderLoader()}
                    <Routes/>
                </div>
            </IconContext.Provider>
        );
    }
}

const mapStateToProps = (state) => {
    return state;
};

// Default export
export default connect(mapStateToProps, personalInfo)(App);
