// Custom imports
import axios from 'axios';

// Global variables
axios.defaults.baseURL = 'http://localhost:8000';
export const PERSONAL_INFO = 'PERSONAL_INFO';

function SetPersonalInfo(personalInfo) {
    return {
        type: 'PERSONAL_INFO',
        personalInfo: personalInfo
    }
}

function SetAbilities(abilities) {
    return {
        type: 'ABILITIES',
        abilities: abilities
    }
}


export function GetPersonalInfo() {
    return (dispatch) => {
        // Get data from wagtail cms here
        return axios
            .get('/api/v2/pages/?type=personalinfo.personalinfo&fields=*')
            .then(data => dispatch(SetPersonalInfo(data.data.items[0])))
            .catch(error => console.log(error));
    }
}

export function GetAbilities() {
    return (dispatch) => {
        // Get data from wagtail cms here
        return axios
            .get('/api/v2/pages/?type=abilities.Abilities&fields=*')
            .then(data => dispatch(SetAbilities(data.data.items)))
            .catch(error => console.log(error));
    }
}
