// Default imports
import React, {Component} from "react";

// Custom imports
import M from "materialize-css";
import "materialize-css/dist/css/materialize.min.css";

class Modal extends Component {
    // Component load
    componentDidMount() {
        const options = {
            inDuration: 250,
            outDuration: 250,
            opacity: 0.5,
            dismissible: true,
            startingTop: "4%",
            endingTop: "10%"
        };
        M.Modal.init(this.Modal, options);
    }

    renderHeading = () => {
        const {Heading} = this.props;
        if (Heading) {
            return <h4>{Heading}<i className="modal-close material-icons right">close</i></h4>
        } else {
            return null;
        }
    };

    renderContent = () => {
        const {Content} = this.props;
        if (Content) {
            return <span dangerouslySetInnerHTML={{__html: Content}}>
            </span>
        } else {
            return null;
        }
    }

    render() {
        return (
            <div>
                <div ref={Modal => {
                    this.Modal = Modal;
                }} id="Modal" className="modal">
                    <div className="modal-content">
                        {this.renderHeading()}
                        {this.renderContent()}
                    </div>
                </div>
            </div>
        );
    }
}

export default Modal;
