// Default imports
import React, {Component} from 'react';

// Custom imports
import axios from 'axios';
import Nav from '../navbar/navbar';
import {connect} from "react-redux";
import * as Action from '../../actions/action';
import './obout.css';
import Footer from "../../components/footer/footer";
import Icon from 'material-icons';

class About extends Component {
    // Component load
    componentDidMount() {
        this.props.GetAbilities();
    }

    renderAbilities = () => {
        const {abilities} = this.props;
        const data = abilities.map(({id, title, abilityDescription, abilityIcon}) => (
            // Html build here
            <div className='col s12 m6 l3 center-align' id={id}>
                <span className="material-icons">{abilityIcon}</span>
                <br/>
                <span className="card-title">{title}</span>
                <br/>
                <span dangerouslySetInnerHTML={{__html: abilityDescription}}></span>
            </div>
        ));
        return data;
    };

    renderMe = () => {
        const {description, personalImageField} = this.props.personalInfo;
        return (
            <div className='card hoverable'>
                <div className='card-content row'>
                    <img alt='person' src={axios.defaults.baseURL + personalImageField.url} className='col s12 m6 l6'/>
                    <div className='col s12 m6 l6' id='description'>
                        {description}
                    </div>
                </div>
            </div>
        )
    };

    render() {
        return (
            // HTML here
            <div>
                <Nav/>
                <div className='container'>
                    <div className="section">
                        <div className='row'>
                            <h4>More about me...</h4>
                        </div>
                    </div>
                    <div className="divider"></div>
                    <div className='row'>{this.renderAbilities()}</div>
                    {this.renderMe()}
                </div>
                <Footer/>
            </div>
        )
    };
}

const mapStateToProps = (state) => {
    return state;
};
// Default export
export default connect(mapStateToProps, Action)(About);
