// Default imports
import React, {Component} from 'react';

// Custom imports
import axios from 'axios';
import Nav from '../navbar/navbar';
import {connect} from "react-redux";
import * as Action from '../../actions/action';
import './obout.css';
import Footer from "../../components/footer/footer";
import 'material-icons';

class About extends Component {
    // Component load
    componentDidMount() {
        this.props.GetAbilities();
        this.props.GetSkills();
    }

    renderSkills=()=>{
        const {skills}=this.props;
        return skills.map(data=>(<span className='btn'>{data.title}</span>))
    };

    renderAbilities = () => {
        const {abilities} = this.props;
        return abilities.map(({id, title, abilityDescription, abilityIcon}) => (
            // Html build here
            <div className='col s12 m6 l3 center-align' id={id}>
                <div className='card hoverable'>
                    <div className='card-content'>
                        <span className="material-icons" id='Icon'>{abilityIcon}</span>
                        <span className="card-title">{title}</span>
                        <span dangerouslySetInnerHTML={{__html: abilityDescription}}></span>
                    </div>
                </div>
            </div>
        ));
    };

    renderMe = () => {
        const {description, personalImageField, firstName, lastName} = this.props.personalInfo;
        return (
            <div className='card hoverable col' id='me'>
                <div className='card-content row'>
                    <div className='card-title center'>{firstName + ' ' + lastName}</div>
                    <div className='col s12 m6 l6' id='description'>{description}</div>
                    <div className='col s12 m6 l6'>
                        {this.renderSkills()}
                    </div>
                </div>
            </div>
        )
    };

    render() {
        return (
            // HTML here
            <div>
                <Nav/>
                <div className='container'>
                    <div className="section">
                        <div className='row'>
                            <h4>More about me...</h4>
                        </div>
                    </div>
                    <div className="divider"></div>
                    <div className='row'>
                        {this.renderAbilities()}
                        {this.renderMe()}
                    </div>
                </div>
                <Footer/>
            </div>
        )
    };
}

const mapStateToProps = (state) => {
    return state;
};
// Default export
export default connect(mapStateToProps, Action)(About);
