// Default imports
import React, {Component} from 'react';

// Custom imports
import './home.scss';
import {NavLink} from "react-router-dom";
import {connect} from 'react-redux';
import * as personalInfo from '../../actions/action';
import $ from "jquery";
import Loader from "../../components/loader/loader";

class Home extends Component {
    // Form load
    componentDidMount() {
        $('.sidenav-overlay').trigger('click');
    }

    renderLoader = () => {
        const {personalInfo} = this.props;
        if (personalInfo==={}) {
            return (
                <Loader/>
            );
        } else {
            return null;
        }
    }

    render() {
        const {firstName, lastName} = this.props.personalInfo;
        return (
            // HTML here
            <div>
                {this.renderLoader()}
                <div className='bg'>
                    <div className="contentCenter">
                        <div className='fontSize'>
                            Hey, I'm <b>{firstName + ' ' + lastName}</b>.<br/>
                            I'm a full-stack web developer.
                        </div>
                        <br/>
                        <NavLink to='/about' className="waves-effect waves-light btn-flat white">
                            Find out more.
                        </NavLink>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return state
};

// Default export
export default connect(mapStateToProps, personalInfo)(Home);
