// Default imports
import React, {Component} from 'react';

// Custom imports
import Nav from '../navbar/navbar';
import {connect} from "react-redux";
import * as Action from '../../actions/action';

class About extends Component {
    // Component load
    componentDidMount() {
        this.props.GetAbilities();
    }

    renderAbilities = () => {
        const {abilities} = this.props;
        return abilities.map(ability => (
            // Html build here
            <div className='card hoverable col s12 m6 l3 center' id={ability.id}>
                <div className='card-content'>
                    <span>Icon: {ability.abilityIcon}</span>
                    <span className="card-title">{ability.title}</span>
                    {ability.abilityDescription}
                </div>
            </div>
        ));
    };

    render() {
        return (
            // HTML here
            <div>
                <Nav/>
                <div className='container'>
                    <div className='row'>{this.renderAbilities()}</div>
                </div>
            </div>
        )
    };
}

const mapStateToProps = (state) => {
    return state;
};
// Default export
export default connect(mapStateToProps, Action)(About);
