// Default imports
import React, {Component} from 'react';

// Custom imports
import Nav from '../../components/navbar/navbar';
import axios from 'axios';
import './portfolio.css';
import Octicon, {GitBranch} from "@primer/octicons-react";

class Portfolio extends Component {
    // default constructor
    constructor() {
        super();
        this.state = {gitData: []}
    }

    componentDidMount() {
        // Get data from github api here
        axios
            .get('https://api.github.com/users/neeravpanchal26/repos')
            .then(data => this.setState({gitData: data.data}))
            .catch(error => console.log(error));
    }

    renderRepos = () => {
        const dataSource = this.state.gitData;
        dataSource.reverse();
        return dataSource.map(repo => (
            // Html build here
            <div className='card'>
                <div className='card-content'>
                    <a href={repo.html_url} className="card-title">{repo.name}</a>
                    <p>{repo.description}</p>
                    <div className="card-action">
                        <span className='blue-text'>{repo.language} â€¢ </span>
                        <span>Latest commit at:
                            {
                                new Intl.DateTimeFormat('en-ZA',{
                                    year: "numeric",
                                    month: "long",
                                    day: "2-digit"
                                })}
                                repo.pushed_at
                        </span>
                        <span className='right'>
                            <Octicon icon={GitBranch}/>
                            {repo.default_branch}
                        </span>
                    </div>
                </div>
            </div>
        ));
    }

    render() {
        return (
            // HTML here
            <div>
                <Nav/>
                <div className='container'>
                    <div className="section">
                        <div className='row'>
                            <h5>My Projects</h5>
                        </div>
                    </div>
                    <div className="divider"></div>
                    {this.renderRepos()}
                </div>
            </div>
        )
    }
}

// Default export
export default Portfolio;
