const initState = {
    personalInfo: {firstName: 'nothing'}
};

const personalInfoReducer = (state = initState, action) => {
    if (action.type === 'PERSONAL_INFO') {
        return {...state, personalInfo: action.personalInfo}
    } else {
        return {...state}
    }
};

export default personalInfoReducer;
