// Default imports
import React, {Component} from 'react';

// Custom imports
import axios from 'axios';
import './portfolio.css';
import Nav from '../navbar/navbar';
import {GoGitBranch, GoKebabVertical} from "react-icons/go";
import {connect} from 'react-redux';
import * as personalInfo from '../../actions/action';
import Footer from "../../components/footer/footer";
import Loader from "../../components/loader/loader";

class Portfolio extends Component {
    // default constructor
    constructor() {
        super();
        this.state = {
            gitData: [],
            loader: true
        }
    };

    // Form load
    componentDidMount() {
        const {gitApiLink} = this.props.personalInfo;
        // Get data from github api here
        axios
            .get(gitApiLink)
            .then(data => this.setState({gitData: data.data, loader: false}))
            .catch(error => console.log(error));
    };

    renderLoader = () => {
        if (this.state.loader) {
            return (
                <Loader/>
            );
        } else {
            return null;
        }
    };

    renderRepos = () => {
        return this.state.gitData
            .reverse()
            .map(repo => (
                // Html build here
                <div className='col s12 m6 l4'>
                    <div className='card hoverable' id={repo.id}>
                        <div className='card-image center'>
                            <span className='letter'>{repo.language.slice(0,1)}</span>
                            <br/>
                            {repo.language}
                        </div>
                        <div className='card-content'>
                            <span className="card-title activator">{repo.name}<i className="material-icons right">more_vert</i></span>
                            <div className="card-action">
                                <span>Latest commit at: {new Date(repo.pushed_at).toUTCString().slice(0, 25)}</span>
                                {repo.html_url}
                                <span className='right'>
                                    {repo.default_branch}
                                    <GoGitBranch size={14}/>
                        </span>
                            </div>
                        </div>
                        <div className='card-reveal'>
                            <span className="card-title">{repo.name}<i className="material-icons right">close</i></span>
                            <p>{repo.description}</p>
                        </div>
                    </div>
                </div>
            ));
    };

    render() {
        return (
            // HTML here
            <div>
                <Nav/>
                {this.renderLoader()}
                <div className='container'>
                    <div className="section">
                        <div className='row'>
                            <h4>My Projects</h4>
                        </div>
                    </div>
                    <div className="divider"></div>
                    <div className='row'>{this.renderRepos()}</div>
                    <Footer/>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return state
};

// Default export
export default connect(mapStateToProps, personalInfo)(Portfolio);
